$tablica = array (0 => 100, "kolor" => "czerwony");
print_r(array_keys ($tablica));

$tablica = array ("niebieski", "czerwony", "zielony", "niebieski", "niebieski");
print_r(array_keys ($tablica, "niebieski"));

