[1] => Array ( [0] => 2 [1] => two [2] => dos [3] => dwa )
[2] => Array ( [0] => 3 [1] => three [2] => tres [3] => trzy )
[3] => Array ( [0] => 4 [1] => four [2] => cuatro [3] => cztery )
[4] => Array ( [0] => 5 [1] => five [2] => cinco [3] => piêæ )
