example.programlisting:
foreach(range(0, 9) as $liczba) {
    echo $liczba;
}
foreach(range('a', 'z') as $litera) {
    echo $litera;
}
foreach(range('z', 'a') as $litera) {
    echo $litera;
}

# do odwrócenia kolejno¶ci sekwencji mo¿na u¿yæ array_reverse
foreach(array_reverse(range(0,9)) as $liczba) {
    echo $liczba;
}

# funkcja array_map() mo¿e byæ u¿yta do zamiany liczb ca³kowitych na znaki
# u¿ywaj±c chr()
foreach(array_map('chr', range(ord('a'),ord('z'))) as $znak){
    echo $znak;
}
