function cmp ($a, $b) {
    if ($a == $b) return 0;
    return ($a > $b) ? -1 : 1;
}

$a = array (3, 2, 5, 6, 1);

usort ($a, "cmp");

while (list ($key, $value) = each ($a)) {
    echo "$key: $value\n";
}

function cmp ($a, $b) {
    return strcmp($a["owoc"], $b["owoc"]);
}

$owoce[0]["owoc"] = "cytryny";
$owoce[1]["owoc"] = "jab³ka";
$owoce[2]["owoc"] = "winogrona";

usort($owoce, "cmp");

while (list ($key, $value) = each ($owoce)) {
    echo "\$owoce[$key]: " . $value["owoc"] . "\n";
}

class TestObj {
    var $name;

    function TestObj($name)
    {
        $this->name = $name;
    }

    /* Statyczna funkcja porównuj±ca */
    function cmp_obj($a, $b) 
    {
        $al = strtolower($a->name);
        $bl = strtolower($b->name);
        if ($al == $bl) return 0;
        return ($al > $bl) ? +1 : -1;
    }
}

$a[] = new TestObj("c");
$a[] = new TestObj("b");
$a[] = new TestObj("d");

uasort($a, array ("TestObj", "cmp_obj"));

foreach ($a as $item) {
    print $item->name."\n";
}
