    bzread przyk³ad


<?php

$file = "/tmp/foo.bz2";
$bz = bzopen($file, "r") or die("Nie mogê otworzyæ $file");

$decompressed_file = '';
while (!feof($bz)) {
  $decompressed_file .= bzread($bz, 4096);
}
bzclose($bz);

echo "Zawarto¶æ pliku $file to: <br />\n";
echo $decompressed_file;

?>
