      Przyk³ad u¿ycia get_class_methods


<?php

class mojaklasa {
    // konstruktor
    function mojaklasa() {
        return(true);
    }

    // metoda 1
    function mojafun1() {
        return(true);
    }

    // metoda 2
    function mojafun2() {
        return(true);
    }
}

$metody_klasy = get_class_methods('mojaklasa');
// lub
$metody_klasy = get_class_methods(new mojaklasa());

foreach ($metody_klasy as $nazwa_metody) {
    echo "$nazwa_metody\n";
}

?>


      &example.outputs; 


