  &reftitle.examples;



    opendir example


<?php
$dir = "/etc/php5/";

// Otwarcie prawid³owego katalogu i pobranie jego zawarto¶ci
if (is_dir($dir)) {
    if ($dh = opendir($dir)) {
        while (($file = readdir($dh)) !== false) {
            echo "filename: $file : filetype: " . filetype($dir . $file) . "\n";
        }
        closedir($dh);
    }
}
?>


