    Przygotowanie instrukcji, wykonanie, i wy¶wietlenie wyniku


<?php
$conn = ovrimos_connect("db_host", "8001", "admin", "password");
if ($conn != 0) {
    echo "Po³±czenie zosta³o nawi±zane.";
    $res = ovrimos_prepare($conn, "select table_id, table_name
                                    from sys.tables where table_id = 7");
    if ($res != 0) {
        echo "Instrukcja zosta³a przygotowana prawid³owo";
        if (ovrimos_execute($res, array(3))) {
            echo "Wykonanie instrukcji powiod³o siê\n";
            ovrimos_result_all($res);
        } else {
            echo "Wykonanie instrukcji nie powiod³o siê";
        }
        ovrimos_free_result($res);
    } else {
        echo "Przygotowanie instrukcji nie powiod³o siê\n";
    }
    ovrimos_close($conn);
}
