    Pobieranie informacji na temat pól


<?php
  $polaczenie_z_baza = pg_connect("dbname=wydawca") or die("Nie mo¿na siê po³±czyæ.");

  $wyn = pg_query($polaczenie_z_baza, "select * from autorzy where autor = 'Orwell'");
  $i = pg_num_fields($wyn);
  for ($j = 0; $j < $i; $j++) {
      echo "kolumna $j\n";
      $nazwa_pola = pg_field_name($wyn, $j);
      echo "nazwa pola: $nazwa_pola\n";
      echo "d³ugo¶æ drukowalna: " . pg_field_prtlen($wyn, $nazwa_pola) . " znaki(ów)\n";
      echo "realna d³ugo¶æ: " . pg_field_size($wyn, $j) . " bajty(ów)\n";
      echo "typ pola: " . pg_field_type($wyn, $j) . " \n\n";
  }
?>


