para.example:
    pobranie kodu PHP z dokumentu zawieraj±cego HTML i PHP


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>titu³</title>'; ?>
</head>
<body>

<?php
echo 'Witaj ¶wiecie!';
?>

</body></html>
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

get_php($tidy->html());


function get_php($node) {

    // sprawdza czy bie¿±cy wêze³ jest kodem PHP
    if($node->isPhp()) {
        echo "\n\n# PHP node #" . ++$GLOBALS['num'] . "\n";
        echo $node->value;
    }

    // sprawdza czy bie¿±cy wêze³ posiada potomków
    if($node->hasChildren()) {
        foreach($node->child as $child) {
            get_php($child);
        }
    }
}

?>


